export const cacheArray = { current: [], index: 0 };
export const getOrAddHook = (initializer) => {
    const hook = cacheArray.current[cacheArray.index];
    if (!hook) {
        const newhook = initializer();
        cacheArray.current[cacheArray.index] = newhook;
        cacheArray.index++;
        return newhook;
    }
    cacheArray.index++;
    return hook;
};
